const modal=document.querySelector("#about-modal"),modalBtn=document.querySelector("#modal-btn"),closeBtn=document.querySelector(".close");function openModal(){modal.style.display="block"}function closeModal(){modal.style.display="none"}function outsideClick(o){o.target==modal&&(modal.style.display="none")}modalBtn.addEventListener("click",openModal),closeBtn.addEventListener("click",closeModal),window.addEventListener("click",outsideClick);